import * as React from 'react';
import PropTypes from 'prop-types';

import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';

import Box from '@mui/material/Box';
import { Typography } from '@mui/material';

const dataForTabs = [
    {key : 0, day: 'monday', temp: '18', city: 'Mumbai'},
    {key : 1,day: 'Tuesday', temp: '20', city: 'Mumbai'},
    {key : 2,day: 'Wednesday', temp: '26', city: 'Mumbai'},
    {key : 4,day: 'Friday', temp: '30', city: 'Mumbai'},
    {key : 3,day: 'Thursday', temp: '32', city: 'Mumbai'},

    {key: 0,day: 'Monday', temp: '26',city: 'Bangalore'},
    {key: 1,day: 'Tuesday', temp: '27',city: 'Bangalore'},
    {key: 2,day: 'Wednesday', temp: '24',city: 'Bangalore'},
    {key: 3,day: 'Thursday', temp: '22',city: 'Bangalore'},
    {key: 4,day: 'Friday', temp: '30',city: 'Bangalore'},

    {key: 0,day: 'Monday', temp: '32', city: 'Delhi'},
    {key: 1,day: 'Tuesday', temp: '28', city: 'Delhi'},
    {key: 2,day: 'wednesday', temp: '30', city: 'Delhi'},
    {key: 3,day: 'Thursday', temp: '15', city: 'Delhi'},
    {key: 4,day: 'Friday', temp: '37', city: 'Delhi'},

    {key: 0,day: 'Monday', temp: '26',city: 'Hyderabad'},
    {key: 1,day: 'Tuesday', temp: '29',city: 'Hyderabad'},
    {key: 2,day: 'Wednesday', temp: '32',city: 'Hyderabad'},
    {key: 3,day: 'Thursday', temp: '36',city: 'Hyderabad'},
    {key: 4,day: 'Friday', temp: '39',city: 'Hyderabad'},

    {key: 0,day: 'Monday', temp: '29',city: 'Chennai'},
    {key: 1,day: 'Tuesday', temp: '34',city: 'Chennai'},
    {key: 2,day: 'Wednesday', temp: '33',city: 'Chennai'},
    {key: 3,day: 'Thursday', temp: '39',city: 'Chennai'},
    {key: 4,day: 'Friday', temp: '42',city: 'Chennai'}
]

export default function BasicTabs (props) {
const newData = dataForTabs.filter(i => i.city === props.city)

  const [value, setValue] = React.useState(0);
  const handleChange = (event, newValue) => {

    setValue(newValue);
  };


function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
        <div
          role="tabpanel"
          hidden={value !== index}
          id={`simple-tabpanel-${index}`}
          aria-labelledby={`simple-tab-${index}`}
          {...other}
        >
          {value === index && (
            <Box sx={{ p: 3 }}>
              <Typography>{children}</Typography>
            </Box>
          )}
        </div>
      );
    }


TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.number.isRequired,
    value: PropTypes.number.isRequired,
  };


function a11yProps(index) {
    return {
        id: `simple-tab-${index}`,
        'aria-controls': `simple-tabpanel-${index}`,
    };
}

    return (
        <Box sx={{ width: '60%',marginLeft:'20vw' }}>
            <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                <Tabs value={value} onChange={handleChange} >
                    <Tab label="Monday" {...a11yProps(0)} />
                    <Tab label="Tuesday" {...a11yProps(1)} />
                    <Tab label="Wednesday" {...a11yProps(2)} />
                    <Tab label="Thursday" {...a11yProps(3)} />
                    <Tab label="Friday" {...a11yProps(4)} />
                </Tabs>
            </Box>
            {newData.map((data) =>{
                {console.log(data.key, "days")}
                return(<TabPanel value={value} index={data.key}>
                    <span>Temperature:{data.temp}</span>
                    <span>Day:{data.day}</span>
                    <span>City:{data.city}</span>
                </TabPanel>)
            })}

        </Box>
    );
}
